app.factory('MarkerCreatorService', function() {

    var markerId = 0;

    function create(latitude, longitude) {
        var marker = {
            options: {
                animation: 1,
                labelAnchor: "28 -5",
                labelClass: 'markerlabel'
            },
            latitude: latitude,
            longitude: longitude,
            id: ++markerId
        };
        return marker;
    }

    function invokeSuccessCallback(successCallback, marker, results) {
        if (typeof successCallback === 'function') {
            successCallback(marker, results);
        }
    }

    function createByCoords(latitude, longitude, successCallback) {
        var marker = create(latitude, longitude);
        invokeSuccessCallback(successCallback, marker);
    }

    function createByAddress(address, successCallback) {
        var geocoder = new google.maps.Geocoder();
        geocoder.geocode({
            'address': address
        }, function(results, status) {
            if (status === google.maps.GeocoderStatus.OK) {
                var firstAddress = results[0];
                var latitude = firstAddress.geometry.location.lat();
                var longitude = firstAddress.geometry.location.lng();
                var marker = create(latitude, longitude);
                invokeSuccessCallback(successCallback, marker, results);
            } else {
                alert("Unknown address: " + address);
            }
        });
    }

    function createByCurrentLocation(successCallback) {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(function(position) {
                var marker = create(position.coords.latitude, position.coords.longitude);
                invokeSuccessCallback(successCallback, marker);
            });
        } else {
            alert('Unable to locate current position');
        }
    }

    return {
        createByCoords: createByCoords,
        createByAddress: createByAddress,
        createByCurrentLocation: createByCurrentLocation
    };

});
app.factory('geocodingService', function($localStorage, $q, $timeout) {

	var locations = $localStorage.locations ? JSON.parse($localStorage.locations) : {};

	var queue = [];

	// Amount of time (in milliseconds) to pause between each trip to the
	// Geocoding API, which places limits on frequency.
	var queryPause = 250;
	/**
	 * executeNext() - execute the next function in the queue.
	 *                  If a result is returned, fulfill the promise.
	 *                  If we get an error, reject the promise (with message).
	 *                  If we receive OVER_QUERY_LIMIT, increase interval and try again.
	 */
	var executeNext = function() {
		var task = queue[0],
			geocoder = new google.maps.Geocoder();

		geocoder.geocode({
			address: task.address
		}, function(result, status) {
			if (status === google.maps.GeocoderStatus.OK) {
				var latLng = {
					lat: result[0].geometry.location.lat(),
					lng: result[0].geometry.location.lng()
				};

				queue.shift();

				locations[task.address] = latLng;
				$localStorage.locations = JSON.stringify(locations);

				task.d.resolve(latLng);

				if (queue.length) {
					$timeout(executeNext, queryPause);
				}
			} else if (status === google.maps.GeocoderStatus.ZERO_RESULTS) {
				queue.shift();
				task.d.reject({
					type: 'zero',
					message: 'Zero results for geocoding address ' + task.address
				});
			} else if (status === google.maps.GeocoderStatus.OVER_QUERY_LIMIT) {
				queryPause += 250;
				$timeout(executeNext, queryPause);
			} else if (status === google.maps.GeocoderStatus.REQUEST_DENIED) {
				queue.shift();
				task.d.reject({
					type: 'denied',
					message: 'Request denied for geocoding address ' + task.address
				});
			} else if (status === google.maps.GeocoderStatus.INVALID_REQUEST) {
				queue.shift();
				task.d.reject({
					type: 'invalid',
					message: 'Invalid request for geocoding address ' + task.address
				});
			}
		});
	};


	//Lay vi tri hien tai
	var currentPosition = function(successCallback) {
		var deffered = $q.defer();
		if (navigator.geolocation) {
			navigator.geolocation.getCurrentPosition(function(position) {
				deffered.resolve(position);
			});
		} else {
			deffered.reject('Unable to locate current position');
		}
		return deffered.promise;
	};

	//Chuyen toa do sang dia chi
	var addressFromLocation = function codeLatLng(lat, lng) {
		var deffered = $q.defer();
		var latlng = new google.maps.LatLng(lat, lng);
		geocoder = new google.maps.Geocoder();
		geocoder.geocode({
			'latLng': latlng
		}, function(results, status) {
			if (status == google.maps.GeocoderStatus.OK) {
				if (results[1]) {
					deffered.resolve(results[1].formatted_address);
				} else {
					deffered.reject("Không thể tìm thấy địa chi");
				}
			} else {
				deffered.reject("Không thể tìm thấy địa chi");
			}
		});
		return deffered.promise;
	};

	return {
		latLngForAddress: function(address) {
			var d = $q.defer();
			if (_.has(locations, address)) {
				$timeout(function() {
					d.resolve(locations[address]);
				});
			} else {
				queue.push({
					address: address,
					d: d
				});

				if (queue.length === 1) {
					executeNext();
				}
			}

			return d.promise;
		},
		currentPosition: currentPosition,
		addressFromLocation: addressFromLocation,
	};
});
